use crate::{Config, Db, ValueEntry}; use tokio::io::AsyncWriteExt; use tokio::net::TcpStream; pub enum Command { PING, ECHO(String), GET(String), SET { key: String, value: String, px: Option&lt;u64&gt;, ex: Option&lt;u64&gt; }, CONFIG(ConfigCommand), } pub enum ConfigCommand { GET(String), } impl Command { pub fn parse_message(message: &amp;str) -&gt; Result&lt;Command, String&gt; { let mut lines = message.lines(); let first_line = lines.next().ok_or(&#34;Argument Error : Empty message&#34;)?; if first_line.starts_with(&#39;*&#39;) { let num_args: usize = first_line[1..].parse().map_err(|_| &#34;Invalid array size&#34;)?; let mut args = Vec::new(); for _ in 0..num_args { let bulk_len_line = lines.next().ok_or(&#34;Missing bulk length&#34;)?; if !bulk_len_line.starts_with(&#39;$&#39;) { return Err(&#34;Invalid bulk string format&#34;.into()); } let bulk_len: usize = bulk_len_line[1..].parse().map_err(|_| &#34;Invalid bulk length&#34;)?; let bulk_string = lines.next().ok_or(&#34;Missing bulk string&#34;)?; if bulk_string.len() != bulk_len { return Err(&#34;Bulk string length mismatch&#34;.into()); } args.push(bulk_string.to_string()); } if let Some(command_name) = args.get(0).map(|s| s.as_str()) { match command_name { &#34;PING&#34; =&gt; Command::parse_ping(&amp;args), &#34;ECHO&#34; =&gt; Command::parse_echo(&amp;args), &#34;GET&#34; =&gt; Command::parse_get(&amp;args), &#34;SET&#34; =&gt; Command::parse_set(&amp;args), &#34;CONFIG&#34; =&gt; Command::parse_config(&amp;args), _ =&gt; Err(format!(&#34;Unknown command: {}&#34;, command_name)), } } else { Err(&#34;Empty command&#34;.into()) } } else { Err(&#34;Unsupported protocol type&#34;.into()) } } pub async fn handle_command(&amp;self, stream: &amp;mut TcpStream, db: Db, config: Config) -&gt; std::io::Result&lt;()&gt; { let response = self.execute(db, config).await; stream.write_all(response.as_bytes()).await?; Ok(()) } pub async fn execute(&amp;self, db: Db, config: Config) -&gt; String { match self { Command::PING =&gt; &#34;+PONG
