use crate::command::Command; use crate::{Config, Db}; use std::sync::Arc; use tokio::io::AsyncReadExt; use tokio::net::TcpStream; pub async fn handle_client(mut stream: TcpStream, db: Db, config: Config) { let mut buffer = [0; 512]; loop { buffer.fill(0); match stream.read(&amp;mut buffer).await { Ok(0) =&gt; break, Ok(n) =&gt; { let message = match std::str::from_utf8(&amp;buffer[..n]) { Ok(msg) =&gt; msg, Err(_) =&gt; { println!(&#34;Failed to parse message as UTF-8&#34;); continue; } }; println!(&#34;Received message: {:?}&#34;, message); match Command::parse_message(message) { Ok(command) =&gt; { if let Err(e) = command.handle_command(&amp;mut stream, Arc::clone(&amp;db), Arc::clone(&amp;config)).await { println!(&#34;Failed to send response: {}&#34;, e); } } Err(e) =&gt; { println!(&#34;Failed to parse command: {}&#34;, e); } } } Err(e) =&gt; { println!(&#34;Error reading from stream: {}&#34;, e); break; } } } } pub async fn handle_env(args: Vec&lt;String&gt;, config: Config) -&gt; Result&lt;(), String&gt; { if args.len() &lt;= 1 { println!(&#34;No configuration arguments provided. Using default settings.&#34;); return Ok(()); } let mut dir: Option&lt;String&gt; = None; let mut path_name: Option&lt;String&gt; = None; let mut arg_index = 1; while arg_index &lt; args.len() { match args[arg_index].as_str() { &#34;--dir&#34; =&gt; { if arg_index + 1 &lt; args.len() { dir = Some(args[arg_index + 1].clone()); arg_index += 2; } else { return Err(&#34;Argument Error: --dir option requires an argument&#34;.into()); } } &#34;--dbfilename&#34; =&gt; { if arg_index + 1 &lt; args.len() { path_name = Some(args[arg_index + 1].clone()); arg_index += 2; } else { return Err(&#34;Argument Error: --dbfilename option requires an argument&#34;.into()); } } _ =&gt; return Err(format!(&#34;Argument Error: &#39;{}&#39; is an unknown option&#34;, args[arg_index])), } } match (dir, path_name) { (Some(dir), Some(path_name)) =&gt; { let mut config_guard = config.write().await; config_guard.insert(&#34;dir&#34;.to_string(), dir); config_guard.insert(&#34;dbfilename&#34;.to_string(), path_name); println!(&#34;Environment configuration applied.&#34;); } (None, None) =&gt; { println!(&#34;No configuration arguments provided. Using default settings.&#34;); } _ =&gt; { return Err(&#34;Argument Error: Both --dir and --dbfilename must be provided together.&#34;.into()); } } Ok(()) } value_entry.rs
use std::time::{Duration, Instant}; #[derive(Clone)] pub struct ValueEntry { pub(crate) value: String, expiration: Option&lt;Instant&gt;, } impl ValueEntry { pub fn new(value: String, ex: Option&lt;u64&gt;, px: Option&lt;u64&gt;) -&gt; ValueEntry { let expiration = match (px, ex) { (Some(ms), _) =&gt; Some(Instant::now() + Duration::from_millis(ms)), (_, Some(s)) =&gt; Some(Instant::now() + Duration::from_secs(s)), _ =&gt; None, }; ValueEntry { value, expiration } } pub fn is_expired(&amp;self) -&gt; bool { if let Some(expiration) = self.expiration { Instant::now() &gt; expiration } else { false } } } main.rs
